import React from "react";
import "./checkout.styles.scss";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import {
  selectCartItems,
  selectCartTotal,
} from "../../redux/cart/cartSelectors.js";
import CheckoutItem from "../../components/checkout-item/CheckoutItem.js";
import StripeButton from "../../components/stripe-button/StripeButton.js"

const CheckoutPage = ({ cartItems, total }) => (
  <>
  <div className="checkout-page">
    <div className="checkout-header">
      <div className="header-block">
        <span>Product</span>
      </div>
      <div className="header-block">
        <span>Description</span>
      </div>
      <div className="header-block">
        <span>Quantity</span>
      </div>
      <div className="header-block">
        <span>Price</span>
      </div>
      <div className="header-block">
        <span>Remove</span>
      </div>
    </div>
    {cartItems.map((cartItem) => (
      <CheckoutItem key={cartItem.id} cartItem={cartItem} />
    ))}
    <div className="total">
      <span>TOTAL:${total}</span>
      <StripeButton price={total}/>
    </div>
    <div>
    <h2 style={{color: "red"}}>*Please use the following test credit card for payments*</h2>
  <h2 style={{color: "red"}}>4242 4242 4242 4242 --Exp: 01/22 -- CW: 123</h2>
    </div>
  </div>

  </>
);

const mapStateToProps = createStructuredSelector({
  cartItems: selectCartItems,
  total: selectCartTotal,
});

export default connect(mapStateToProps)(CheckoutPage);
